using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace Cards8SuitsAndValues{    class Program    {        static readonly Random random = new Random();        static void Main()        {            while (true)            {                                List<Card> cards = new List<Card>();                               Console.Write("Enter number of Cards: ");                if (int.TryParse(Console.ReadLine(), out int numberOfCards))                {                    Console.WriteLine();                    for (int i = 0; i < numberOfCards; i++)                        cards.Add(RandomCard());                }                else                {                    Console.WriteLine();                    return;                }                PrintCards(cards);                Console.WriteLine($"\n... sorting the cards ...\n");                CardsComparerByValue comparer = new CardsComparerByValue();                cards.Sort(comparer);                // or just                 //  cards.Sort(new CardsComparerByValue());                PrintCards(cards);            }        }        static Card RandomCard()         {            return new Card((Values)random.Next(1, 14), (Suits)random.Next(0,3));        }        static void PrintCards(List<Card> cards)        {            foreach(Card card in cards)            {                Console.WriteLine($"{cards.IndexOf(card) +1 }. {card.Name}");            }        }    }}